import React, { Component, Fragment } from "react";
import { Container, Col, Row, Card } from "react-bootstrap";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import classes from "./NewArrival.module.css";
import axios from "axios";
import AppURL from "../../api/AppURL";
import NewArrivalLoading from "../PlaceHolder/NewArrivalLoading";
import { Link } from "react-router-dom";

function SampleNextArrow(props) {
  const { className, style, onClick } = props;
  return (
    <div
      className={className}
      style={{
        ...style,
        display: "block",
        background: "#f57224",
        borderRadius: "50%",
        paddingTop: "1px",
      }}
      onClick={onClick}
    />
  );
}

function SamplePrevArrow(props) {
  const { className, style, onClick } = props;
  return (
    <div
      className={className}
      style={{
        ...style,
        display: "block",
        background: "#f57224",
        borderRadius: "50%",
        paddingTop: "1px",
      }}
      onClick={onClick}
    />
  );
}

class NewArrival extends Component {
  constructor(props) {
    super(props);
    this.state = {
      ProductData: [],
      isLoading: "",
      mainDiv: "d-none",
    };
    this.nextButtonHandler = this.nextButtonHandler.bind(this);
    this.prevButtonHandler = this.prevButtonHandler.bind(this);
  }
  nextButtonHandler() {
    this.slider.slickNext();
  }
  prevButtonHandler() {
    this.slider.slickPrev();
  }

  componentDidMount() {
    axios
      .get(AppURL.ProductListByRemark("NEW"))
      .then((response) => {
        this.setState({
          ProductData: response.data,
          isLoading: "d-none",
          mainDiv: "",
        });
      })
      .catch((error) => {});
  }

  render() {
    // Start Slick Carousel
    var settings = {
      dots: false,
      infinite: true,
      speed: 500,
      autoplay: true,
      autoplaySpeed: 3000,
      slidesToShow: 6,
      slidesToScroll: 1,
      initialSlide: 0,
      cssEase: "linear",
      nextArrow: <SampleNextArrow />,
      prevArrow: <SamplePrevArrow />,
      arrows: false,
      responsive: [
        {
          breakpoint: 1024,
          settings: {
            slidesToShow: 3,
            slidesToScroll: 1,
            infinite: true,
            dots: true,
          },
        },
        {
          breakpoint: 600,
          settings: {
            slidesToShow: 2,
            slidesToScroll: 1,
            initialSlide: 2,
          },
        },
        {
          breakpoint: 480,
          settings: {
            slidesToShow: 1,
            slidesToScroll: 1,
          },
        },
      ],
    };
    // End Slick Carousel

    const NewList = this.state.ProductData;
    const MyView = NewList.map((NewList, i) => {
      if (NewList.special_price === "na") {
        return (
          <Col
            key={i.toString()}
            className={`${this.state.mainDiv} mb-2 p-1`}
            xl={2}
            lg={2}
            md={2}
            sm={4}
            xs={6}
          >
            <Link className="custom-links" to={"/productdetails/" + NewList.id}>
              <Card className="card-product">
                <Card.Img variant="top" src={NewList.image} alt="3b-button-3" />
                <Card.Body>
                  <span className="card-product-title">{NewList.title}</span>
                  <br />
                  <span className="card-product-price">₱{NewList.price}</span>
                  <br />
                  <br />
                </Card.Body>
              </Card>
            </Link>
          </Col>
        );
      } else {
        return (
          <Col
            key={i.toString()}
            className={`${this.state.mainDiv} mb-2 p-1`}
            xl={2}
            lg={2}
            md={2}
            sm={4}
            xs={6}
          >
            <Link className="custom-links" to={"/productdetails/" + NewList.id}>
              <Card className="card-product">
                <Card.Img variant="top" src={NewList.image} alt="3b-button-3" />
                <Card.Body>
                  <span className="card-product-title">{NewList.title}</span>
                  <br />
                  <span className="card-product-price">
                    ₱{NewList.special_price}
                  </span>
                  <br />
                  <strike className="card-product-discount">
                    ₱{NewList.price}
                  </strike>
                  <span className="card-product-discount-percent"> -71%</span>
                </Card.Body>
              </Card>
            </Link>
          </Col>
        );
      }
    });

    return (
      <Fragment>
        <Container>
          <h2 className="section-header">
            New Arrivals
            <button
              className={`${classes["next-button"]} btn btn-sm ms-2 float-end`}
              onClick={this.nextButtonHandler}
            >
              <i className="fa fa-angle-right"></i>
            </button>
            <button
              className={`${classes["prev-button"]} btn btn-sm ms-2 float-end`}
              onClick={this.prevButtonHandler}
            >
              <i className="fa fa-angle-left"></i>
            </button>
          </h2>

          {/* Start New Arrival Product Card */}
          <div>
            <Row className="mb-2">
              {/* Start Skeleton Loading PlaceHolder */}
              <NewArrivalLoading isLoading={this.state.isLoading} />
              {/* Start Skeleton Loading PlaceHolder */}
              <Slider ref={(c) => (this.slider = c)} {...settings}>
                {MyView}
              </Slider>
            </Row>
          </div>

          {/* End New Arrival Product Card */}
        </Container>
        <br />
      </Fragment>
    );
  }
}

export default NewArrival;
